const YITH_DEBUGGER_VERSION = '1.2.0';

/* Main Hook to filter loading plugins list */
add_filter( 'option_active_plugins', 'yith_debugger' );

/*
 * Main Function
 *
 * #param $plugins array
 * #return $plugins array
 *
 */
function yith_debugger( $plugins )
{

    /* if we are not doing ajax and the debugger arg yith_ajax_only is isset we don't need to manage the filter */
    if ( ! wp_doing_ajax() && isset( $_GET['yith_ajax_only'] ) ) {
        return $plugins;
    }

    $yith_mu_cmd = ''; //the action to apply
    $list = ''; //for disabling commands
    $switch_theme = '';
    $cookie_creation = false;
    $cookie_name = 'yith_debugger';

    /* remove cookie if passed the related action command*/
    if ( isset($_GET['yith']) && $_GET['yith'] == 'remove_cookie') {
        if (isset($_COOKIE[$cookie_name]) ) {
            unset($_COOKIE[$cookie_name]);
            unset($_COOKIE[$cookie_name]);
            setcookie($cookie_name, null, -1, '/');
            setcookie($cookie_name, null, -1, '/');
        }
    }

    /* check if the yith_debugger cookie exists so to load the settings */
    if (isset($_COOKIE[$cookie_name]) ) {
        $cc = (array)json_decode( $_COOKIE[$cookie_name] );

        $yith_mu_cmd = isset($cc['action']) ? $cc['action'] : '' ;

        if ( isset( $cc['switch_theme']) ) {
            $switch_theme = $cc['switch_theme'];
        }

        if ( isset($cc['list']) ) {
            $list = $cc['list'];
        }

    }

    if ( isset( $_GET['yith'] ) && trim( $_GET['yith'] ) != '' || isset($_COOKIE[$cookie_name]) ) {
        $yith_mu_cmd =  isset( $_GET['yith'] ) ? $_GET['yith'] : $yith_mu_cmd; //the action to apply


        if ( isset( $_GET['create_cookie']) ) {
                /* create a coockie with dashboard value so the debugger will be always active
                until the cookie will be removed or expire */
                $cookie_creation = yith_create_cookie ($cookie_name);
                $yith_mu_cmd = 'dashboard';
        }

        switch ( $yith_mu_cmd ) {
            case 'help': /* show usage */
                yith_debugger_usage();
                break;
            case 'changelog': /* show changelog */
                yith_debugger_changelog();
                break;
            case 'dashboard': /* show debugger dashboard */
                yith_dashboard( $plugins, $cookie_creation, $cookie_name );
                break;
            case 'plugins_list': /* list active plugins */
                yith_plugins_list( $plugins );
                break;
            case 'disable_all': /* disable all plugins */
                return array();
                break;
            case 'disable_this': /* disable selected plugins by index */
                if ( isset( $_GET['list'] ) && trim( $_GET['list'] ) != '' || !empty( trim( $list ) ) ) {
                    $list = (isset( $_GET['list'] ) && trim( $_GET['list'] ) != '') ? trim( $_GET['list'] ) : trim($list);
                    /* check if it could be a single index or more than one */
                    if ( strpos( $list , '-' ) > 0 ) {
                        $list = explode('-',$list);
                    }
                    $plugins = disable_this( $plugins, $list );
                }
                break;
            case 'disable_all_but_not':
                if ( isset( $_GET['list'] ) && trim( $_GET['list'] ) != '' || !empty( trim( $list ) ) ) {
                    /* check if it could be a single index or more than one */
                    $list = (isset( $_GET['list'] ) && trim( $_GET['list'] ) != '') ? trim( $_GET['list'] ) : trim($list);

                    if ( strpos( $list , '-' ) > 0 ) {
                        $list = explode('-',$list);
                    }
                    $plugins = disable_all_but_not( $plugins, $list );
                }
                break;
        }

        /* check option to change theme on the fly */
        if ( isset($_GET['switch_theme']) && $_GET['switch_theme'] != '' || ! empty(trim($switch_theme)) ) {
            $theme_to_set = isset($_GET['switch_theme']) ? $_GET['switch_theme'] : trim($switch_theme);
            add_filter('template', function() use ($theme_to_set) { return $theme_to_set ;});
            add_filter('option_template', function() use ($theme_to_set) { return $theme_to_set ;});
            add_filter('option_stylesheet', function() use ($theme_to_set) { return $theme_to_set ;});
        }

        if ( isset($_GET['yith']) && $_GET['yith'] == 'show_post_metas'  ){

            //id passed
            if ( isset( $_GET['show_post_metas_for'] ) && $_GET['show_post_metas_for'] != '') {
                $by = (isset($_GET['by']) && $_GET['by'] != '' ) ? $_GET['by'] : '';
                yith_dbg_print_metas( yith_dbg_get_post_metas( $_GET['show_post_metas_for'], $by ), $_GET['show_post_metas_for'] );
                die;
            }

        }

    }
    return $plugins;
}


