            urltodebug = document.querySelector("input#urltodebug"), /* input field for the url to debug */
            extra_action_meta = document.querySelector("#extra_action_meta"), /* Extra Action Meta Button */
            show_post_metas_for = document.querySelector('#show_post_metas_for'), /* field with the Post ID to get the the metas */
            show_post_metas_substring = document.querySelector('#show_post_metas_substring');


        /* click event on submit */
