            var actions = document.querySelectorAll("input[name=action]"), /* get actions */
                themes = document.querySelectorAll("#available_themes input[type=radio]"), /* get themes */
                selected_action = '', /* action selected and to pass */
                action_url = '', /* action url - this will be prepared */
                switch_theme_option = document.getElementById("switch_theme_option"), /* get the option to change the theme */
                cookie_creation = document.getElementById('createcookie').checked; /* get the Create Cookie option value */


            /* get selected action*/
            for (i = 0; i < actions.length; ++i) {
                if ( actions[i].checked ) {
                    selected_action = actions[i].value;
                }
            }

            /* get selected theme */
            for (i = 0; i < themes.length; ++i) {
                if ( themes[i].checked ) {
                    selected_theme = themes[i].value;
                }
            }

            /* preparing the url to check */
            if ( urltodebug.value != '' ) {
                action_url = urltodebug.value;
            } else {
                action_url = urldebugger.getAttribute('data-url');
            }
            /* check if the url has just paramaters in that case add our to the code */
            if ( action_url.indexOf('?') > 0 ) {
                action_url = action_url + '&yith=';
            } else {
                /* check if the url has the final slash, or add it */
                if ( urltodebug.value.slice(-1) != '/' ) {action_url = action_url + '/'; }
                action_url = action_url + '?yith=';
            }

            /* manage url arguments by action selected */
            if ( selected_action == 'plugins_list' || selected_action == 'only_switch_theme') {
                action_url = action_url + selected_action;
                if ( selected_action == 'only_switch_theme' ) {
                    /* if the action is only switch theme we set the Switch theme checkbox to true to so to pass for sure the theme slug on final check */
                    switch_theme_option.checked = true;
                }

            } else if ( selected_action == 'disable_this' || selected_action == 'disable_all_but_not' ) {
                var plist = get_plugins_cheked(); //selected plugins list
                if( plist.trim() == '' ) {
                    e.preventDefault();
                    alert('no plugins selected');
                }
                action_url = action_url + selected_action + '&list=' + get_plugins_cheked();
                var options = document.querySelectorAll("input[name=option]"),
                    option = '';
                for (i = 0; i < options.length; ++i) {
                    if ( options[i].checked ) {
                        option = options[i].value;
                    }

                    if ( option.trim() != '' ) { action_url = action_url + '&' + option; }
                }

            }

            /* if we need to switch the theme add the option to the action url */
            if ( switch_theme_option.checked ) {
                action_url = action_url + '&switch_theme=' + selected_theme;
            }
            

            /* add create_cookie variable to the url */
            if ( cookie_creation ) {
                action_url = action_url + '&create_cookie';
            }

            /* set the new url */
            urldebugger.setAttribute('href',action_url );

        });


        /* manage extra actions */
        extra_action_meta.addEventListener('click', show_metas);
        show_post_metas_for.addEventListener('keypress', function(e) {
            if (e.key === 'Enter') {
                show_metas();
            }
        });
        show_post_metas_substring.addEventListener('keypress', function(e) {
            if (e.key === 'Enter') {
                show_metas();
            }
        });

        /* get the id list of checked plugins */
        function get_plugins_cheked() {
            /* get all checkboxes */
            var plugins = document.querySelectorAll("#plugins_list input[type=checkbox]"),
                list = '';
            for (i = 0; i < plugins.length; ++i) {
                if ( plugins[i].checked ) {
                    list += plugins[i].value + '-';
                }
            }
            /* fix the last - in plugins list */
            if ( list.slice(-1) == '-' ) {
                list = list.substr(0, list.length - 1);
            }
            return list;
        }

        /* select and deselect click events */
        /*select all*/
        var selectall = document.getElementById('select_all');
        selectall.addEventListener('click', function(e){
            e.preventDefault();
            for (i = 0; i < checkbox.length; ++i) { checkbox[i].checked = true; }
        });
        /* deselect all */
        var deselectall = document.getElementById('deselect_all');
        deselectall.addEventListener('click', function(e){
            e.preventDefault();
            for (i = 0; i < checkbox.length; ++i) { checkbox[i].checked = false; }
        });
        /* revert selection*/
        var selectionrevert = document.getElementById('selection_revert');
        selectionrevert.addEventListener('click', function(e){
            e.preventDefault();
            for (i = 0; i < checkbox.length; ++i) {
                if ( checkbox[i].checked ) { checkbox[i].checked = false; }
                else { checkbox[i].checked = true; }
            }
        });


        function show_metas(){
            var action_url = '',
                by = document.querySelector('#show_post_metas_substring').value;

            /* preparing the url to check */
            if ( urltodebug.value != '' ) {
                action_url = urltodebug.value;
            } else {
                action_url = urldebugger.getAttribute('data-url');
            }
            /* check if the url has just paramaters in that case add our to the code */
            if ( action_url.indexOf('?') > 0 ) {
                action_url = action_url + '&yith=';
            } else {
                /* check if the url has the final slash, or add it */
                if ( urltodebug.value.slice(-1) != '/' ) {action_url = action_url + '/'; }
                action_url = action_url + '?yith=';
            }

            var show_post_metas_for = document.getElementById('show_post_metas_for').value;
            if ( show_post_metas_for != '' ) {
                var h = document.querySelector('#show_metas_hidden_command');
                action_url +=  'show_post_metas&show_post_metas_for=' + show_post_metas_for;
                if ( by !== '') {
                    action_url +=  '&by=' + by;
                }
                h.href = action_url;
                h.click();

            }
        }
    </script>

    <?php
    die;
} // end dashboard function


/**
 * Create a cookie with all the options selected in dashboard
 *
 * @since 1.1.0
 * @author Armando Liccardo
 * @return array
 */
function yith_create_cookie( $cookie_name ) {
    $result = array( 'status' => false, 'msg' => '');

    $options = array();

    /* checking the passed options */
    if ( isset( $_GET['yith']) && trim($_GET['yith']) != '' ) {
        $options['action'] = trim($_GET['yith']);
    }

    if ( isset( $_GET['switch_theme']) && trim($_GET['switch_theme']) != '' ) {
        $options['switch_theme'] = trim($_GET['switch_theme']);
    }

    if ( isset( $_GET['list']) && trim( $_GET['list']) != '' ) {
        $options['list'] = trim( $_GET['list']);
    }

    $r = setcookie($cookie_name, json_encode($options), time() + (86400 * 30), "/"); // 86400 = 1 day

    if ( $r ) {
        $result['status'] = true;
        $result['msg'] = $options;
    } else {
        $result['msg'] ='cannot write cookie';
    }

    return $result;
}


