 * @param  array $list
 * @return array $plugins
 * @author Armando Liccardo
 * @since  1.0.0
 *
 */
function disable_this( $plugins, $list ) {
    if ( is_array( $list ) ) {
        $to_remove = array();
        foreach ($list as $i ) {
            $to_remove[$i] = $plugins[$i];
        }
        return array_diff($plugins, $to_remove);

    } else {
        unset( $plugins[$list] );
    }
    return $plugins;
}

/**
 * disable all plugins but not the one/s selected by index
 *
 * @param  array $plugins
 * @param  array $list
 * @return array $plugins
 * @author Armando Liccardo
 * @since  1.0.0
 *
 */
function disable_all_but_not( $plugins, $list ) {
    $not_disable = array();
    /* passed array */
    if ( is_array( $list ) ) {
        /* create the array of plugins that we will not disable */
        foreach ($list as $i ) {
            $not_disable[$i] = $plugins[$i];
        }

        /* diff the array and disable all other plugins */
        foreach ($plugins as $i => $path) {
            if (!in_array($path, $not_disable)) {
                unset($plugins[$i]);
            }
        }
    } else {
        /*passed one plugin index */
        $not_disable[0] =  $plugins[$list];
        foreach ($plugins as $i => $path) {
            if (!in_array($path, $not_disable)) {
                unset($plugins[$i]);
            }
        }
    }
    return $plugins;
}

function yith_dbg_get_post_metas( $pid = 0, $by = '' ) {
    if ( $pid != '' ) {
        if ( trim($by) != '' ){
            $metas = get_post_meta( $pid );
            $metas_by = array();
            foreach ( $metas as $k => $v ) {
                if ( strpos($k, $by, 0) >-1 ) {
                    $metas_by[$k] = $v ;
                }
            }

            return $metas_by;
        } else {
            return get_post_meta( $pid );
        }

    }
}


