if ( isset( $_GET['yith'] ) && trim( $_GET['yith'] ) != '' && !isset( $_GET['ex_ajax']) ) {

    add_action('wp_footer','mu_plugin_script');
    function mu_plugin_script() {
        ?>
        <script>

            var xml_type;
            // branch for native XMLHttpRequest object
            if(window.XMLHttpRequest && !(window.ActiveXObject)) {
                xml_type = 'XMLHttpRequest';
            // branch for IE/Windows ActiveX version
            } else if(window.ActiveXObject) {
                try {
                    a = new ActiveXObject('Msxml2.XMLHTTP');
                    xml_type = 'Msxml2.XMLHTTP';
                } catch(e) {
                    a = new ActiveXObject('Microsoft.XMLHTTP');
                    xml_type = 'Microsoft.XMLHTTP';
                }
            }

            var ActualActiveXObject = window.ActiveXObject;
            var ActiveXObject;

            if (xml_type == 'XMLHttpRequest') {

                (function(open) {
                    XMLHttpRequest.prototype.open = function(method, url, async, user, pass) {
                        console.log('yith_mu_debugger_ajax_inject');
                        var thisurl = '';
                        if (url.indexOf('?') > 0 ) {
                            /* we just have paramters in url so we avoid to add a second ? */
                            thisurl = url + '&yith=<?php echo $_GET['yith']; ?>';
                        } else {
                            thisurl = url + '?yith=<?php echo $_GET['yith']; ?>'
                        }
                        <?php if ($_GET['yith'] == 'disable_this' || $_GET['yith'] == 'disable_all_but_not') {  ?>
                            open.call(this, method, thisurl+"&list=<?php echo $_GET['list']; ?>", async, user, pass);
                        <?php } else { ?>
                            open.call(this, method, thisurl, async, user, pass);
                        <?php }
                        ?>
                    };
                })(XMLHttpRequest.prototype.open);
            } else {
                console.log('yith_mu_debugger: it seems we have an  ajax call for IE/Windows ActiveX version. In this case the ajax inject will not  work');
            }
        </script>
    <?php
    }
}